mapper.select:
select d.divname, a.type, a.title, a.content, a.appdivno, a.maccp, a.faccp, a.APPACCPNO, p.accepname, e.name
from APPRDIV d, APPROVAL a, APPROACCP p, EMPLOYEE e
where a.appaccpno = p.appaccpno and a.appdivno = d.appdivno AND a.empno = e.empno AND a.deleted = 'N' and a.appdivno = #{ appdivno }
order by a.type DESC;


SELECT d.divname, a.type, a.empno, a.appdivno, a.title, a.content, a.maccp, a.faccp, a.apprfilename, a.filepath, a.deleted, a.APPACCPNO, p.accepname, e.name
FROM APPRDIV d, APPROVAL a, APPROACCP p, EMPLOYEE e
WHERE a.appaccpno = p.appaccpno and a.appdivno = d.appdivno AND a.empno = e.empno AND a.deleted = 'N' and a.appdivno = #{ appdivno } and a.type = #{typeNo}
