mapper.update:

UPDATE REPLY
SET sno = sno + 1
WHERE gno = #{ gno } AND sno > #{ sno }

mapper.select:
select r.rno, r.empno, r.bno, r.rcontent, r.rdate, r.gno, r.sno, r.depth ,e.name
from REPLY r, EMPLOYEE e
where r.empno = e.empno and r.bno=#{ bno }
ORDER BY gno DESC, sno ASC
LIMIT #{ beginning }, #{ end }
