mapper.select:
SELECT r.empno, r.refno, r.refname, r.refcontent, r.refdate, r.refdel, e.name, e.empno FROM REFERENCE r, EMPLOYEE e WHERE
r.refdel = 'N' and r.empno = e.empno
ORDER BY r.refno DESC LIMIT #{beginning}, #{pageSize}

SELECT refno, reffilename, refpath
FROM REFERENCE
WHERE refno = #{refNo}

mapper.update:
UPDATE REFERENCE
SET refdel = 'Y' 
WHERE refno = #{ refNo }

UPDATE REFERENCE
SET refname = #{ refname }, refcontent = #{ refcontent }, refdate = NOW(), refpath = #{ refpath }, reffilename = #{ reffilename }  
WHERE refno = #{ refno }


UPDATE REFREPLY
SET sno = sno + 1
WHERE gno = #{ gno } AND sno > #{ sno }
